mapper.update:
update sys_permission
set per_name = #{perName,jdbcType=VARCHAR},
  per_type = #{perType,jdbcType=INTEGER},
  ope_id = #{opeId,jdbcType=INTEGER},
  menu_id = #{menuId,jdbcType=INTEGER}
where per_id = #{perId,jdbcType=INTEGER}

mapper.select:
SELECT

FROM sys_permission where 1=1 

	and per_id=#{perId,jdbcType=INTEGER}

SELECT p.per_id as perId,p.per_name as perName,p.per_type as perType,p.ope_id as opeId, p.menu_id as menuId,m.leaf_node as leafNode, m.level as level,m.parent_id as parentId FROM sys_permission p left join sys_menu m on p.menu_id=m.menu_id where 1=1
and m.level = #{level}
order by m.order_number
